import React, { useState, useEffect } from 'react';

import { ImageBackground, Button, View, Text, StyleSheet, TouchableOpacity, TextInput, Platform, Pressable } from 'react-native';

const API_URL = Platform.OS !== 'ios' ? 'http://localhost:5000' : 'http://10.0.2.2:5000';
//const API_URL = 'http://localhost:5000';

const TuneScreen = ({ navigation }) => {

    
    const start = () => {
        let audio = new Audio(require("../../public/sounds/A4.mp3"))
        audio.volume = 1
        audio.play()
        audio.remove()
    }

    return (
        <View style={styles.back}>
            <View style={styles.header}>
                <Text style={styles.headerTitle}>Tune</Text>
            </View>
            < View >
                <TouchableOpacity style={{height: "200px", width: '200px', backgroundColor: 'purple', opacity: '0.75'}} onPressIn={() => start()}></TouchableOpacity>
            </View >
        </View>
    );
};

const styles = StyleSheet.create({
    header: {
        width: '100%',
        height: '10%',
        backgroundColor: '#EBEBEB',
        borderBottomWidth: '1px',
        borderBottomColor: 'black',
    },
    headerTitle: {
        color: 'black',
        fontWeight: 'bold',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginBottom: 'auto',
        marginTop: 'auto',
        fontSize: '25px'
    },
    back: {
        width: '100%',
        height: '100%',
        backgroundColor: '#EBEBEB',
    },
    heading: {
        fontSize: 20,
        fontWeight: 'bold',
        marginTop: '2%',
        marginBottom: '5%',
        textAlign: 'center',
        color: 'black',
    },
});

export default TuneScreen;
