import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import HomeScreen from '../index';
import Tabs from '../../navigation/bottomTab';
import { StackActions } from '@react-navigation/native';
import { ImageBackground, View, Text, StyleSheet, TouchableOpacity, TextInput, Platform, Image } from 'react-native';
//import { Storage, sessionStorage } from '../../storage/Storage';

//import Constants from 'expo-constants';
//const {manifest} = Constants;
//const api = (typeof manifest.packagerOpts === `object`) && manifest.packagerOpts.dev
//  ? manifest.debuggerHost.split(`:`).shift().concat(`:3000`)
//  : `api.example.com`;
//const getStoredItem = (key) => window && window.sessionStorage && sessionStorage.getItem(key)

const API_URL = Platform.OS !== 'ios' ? 'http://localhost:5000' : 'http://192.168.1.118:5000';

const AuthScreen = ({ navigation }) => {

    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');

    const [isError, setIsError] = useState(false);
    const [message, setMessage] = useState('');
    const [isLogin, setIsLogin] = useState(true);

    const onChangeHandler = () => {
        setIsLogin(!isLogin);
        setMessage('');
    };

    const onLoggedIn = token => {
        fetch(`${API_URL}/private`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Origin': 'http://localhost:19006',
                'Access-Control-Request-Method': 'GET',
                'Access-Control-Request-Headers': 'Content-Type, Authorization',
                'Authorization': `Bearer ${token}`,
            },
        })
            .then(async res => {
                try {
                    const jsonRes = await res.json();
                    if (res.status === 200) {
                        setMessage(jsonRes.message);
                        navigation.navigate('MainNav');
                    }
                } catch (err) {
                    console.log(err);
                };
            })
            .catch(err => {
                console.log(err);
            });
    }

    const onSubmitHandler = () => {
        const payload = {
            email,
            name,
            password,
        };
        fetch(`${API_URL}/${isLogin ? 'login' : 'signup'}`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Origin': 'http://localhost:19006',
                'Access-Control-Request-Method': 'POST',
                'Access-Control-Request-Headers': 'Content-Type, Authorization',
            },
            body: JSON.stringify(payload),
        })
            .then(async res => {
                try {
                    const jsonRes = await res.json();
                    if (res.status !== 200) {
                        setIsError(true);
                        setMessage(jsonRes.message);
                    } else {
                        sessionStorage.setItem("name", jsonRes.name);
                        sessionStorage.setItem("email", jsonRes.email);
                        onLoggedIn(jsonRes.token);
                        setIsError(false);
                        setMessage(jsonRes.message);
                    }
                } catch (err) {
                    //setMessage(err);
                    console.log(err);
                };
            })
            .catch(err => {
                console.log(err);
            });
    };

    const getMessage = () => {
        const status = isError ? `Error: ` : `Success: `;
        return status + message;
    }

    return (
        <View style={styles.background}>
            <View style={styles.card}>
                <Text style={styles.heading}>{isLogin ? 'Login' : 'Signup'}</Text>
                <View style={styles.form}>
                    <View style={styles.inputs}>
                        <TextInput style={styles.input} placeholder="Email" autoCapitalize="none" onChangeText={setEmail}></TextInput>
                        {!isLogin && <TextInput style={styles.inputBottom} placeholder="Username" onChangeText={setName}></TextInput>}
                        <TextInput secureTextEntry={true} style={styles.inputBottom} placeholder="Password" onChangeText={setPassword}></TextInput>
                        <TouchableOpacity style={styles.buttonAltTop} onPress={onSubmitHandler}>
                            <Text style={styles.buttonAltText}>Submit</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={styles.buttonAltBottom} onPress={onChangeHandler}>
                            <Text style={styles.buttonAltText}>{isLogin ? 'Sign Up' : 'Log In'}</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    image: {
        marginTop: '3%',
        width: '10%',
        height: '22.5%',
        resizeMode: 'contain',
        //objectFit: 'cover',
        //objectPosition: '50% 50%',
    },
    background: {
        flex: 1,
        width: '100%',
        height: '100%',
        alignItems: 'center',
        backgroundColor: '#EBEBEB',
    },
    card: {
        flex: 1,
        width: '60%',
        //marginTop: '8%',
        borderRadius: 20,
        maxHeight: 380,
        marginBottom: 'auto',
        marginTop: 'auto',
    },
    heading: {
        fontSize: 30,
        fontWeight: 'bold',
        marginLeft: 'auto',
        marginRight: 'auto',
        marginTop: '3%',
        marginBottom: '20%',
        color: 'black',
    },
    form: {
        flex: 1,
        justifyContent: 'space-between',
        paddingBottom: '5%',
    },
    inputs: {
        width: '100%',
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        color: 'black',
    },
    input: {
        width: '80%',
        borderWidth: 1,
        borderColor: 'black',
        paddingTop: 10,
        fontSize: 16,
        minHeight: 40,
        color: 'black',
    },
    inputBottom: {
        width: '80%',
        borderWidth: 1,
        borderColor: 'black',
        borderTopWidth: 0,
        paddingTop: 10,
        fontSize: 16,
        minHeight: 40,
        color: 'black',
    },
    button: {
        width: '80%',
        backgroundColor: 'black',
        height: 40,
        borderRadius: 50,
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical: 5,
    },
    buttonText: {
        color: 'black',
        fontSize: 16,
        fontWeight: '400'
    },
    buttonAltTop: {
        marginTop: '5%',
        width: '80%',
        borderWidth: 1,
        height: 40,
        borderRadius: 50,
        borderColor: 'black',
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical: 5,
    },
    buttonAltBottom: {
        marginTop: '1%',
        width: '80%',
        borderWidth: 1,
        height: 40,
        borderRadius: 50,
        borderColor: 'black',
        justifyContent: 'center',
        alignItems: 'center',
        marginVertical: 5,
    },
    buttonAltText: {
        color: 'black',
        fontSize: 16,
        fontWeight: '400',
    },
    message: {
        fontSize: 16,
        marginVertical: '2%',
        color: 'black',
    },
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});

export default AuthScreen;
