import React, { Component, useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image, Touchable } from 'react-native';


const DropDown = ({
    data = [],
    value = {},
    buttonStyle = {},
    textStyle = {},
    onSelect = () => { }
}) => {

    const [showOption, setShowOption] = useState(false)

    const onSelectedItem = (val) => {
        sessionStorage.setItem("boxName", JSON.stringify(val))
        setShowOption(false)
        onSelect(val)
    }

    return (
        <View style={styles.container}>
            <TouchableOpacity
                style={buttonStyle}
                activeOpacity={0.8}
                onPress={() => setShowOption(!showOption)}
            >
                <Text style={{ color: 'white' }}>{!!value ? value?.name : 'Choose your location'}</Text>
                <Image
                    style={{
                        marginLeft: '315px',
                        position: 'fixed',
                        width: '15px',
                        height: '15px',
                        flexDirection: 'row',
                        resizeMode: 'contain',
                        transform: [{ rotate: showOption ? '180deg' : '0deg' }],
                    }}
                    source={require('../public/images/dropdown_icon.png')}
                />
            </TouchableOpacity>
            {showOption && (<View style={textStyle}>
                {data.map((val, i) => {
                    return (
                        <TouchableOpacity
                            key={String(i)}
                            onPress={() => onSelectedItem(val)}
                        >
                            <Text>{val.name}</Text>
                        </TouchableOpacity>
                    )
                })}
            </View>)}
        </View>
    );

};

const styles = StyleSheet.create({
    container: {
        zIndex: 10,
    },
    image: {
        textAlign: 'right',
        width: '15px',
        height: '15px',
        flexDirection: 'row',
        resizeMode: 'contain',
    },
})

export default DropDown;
